import React, { useState } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Link } from 'react-router-dom';
import axios from "axios";
import { useNavigate } from 'react-router-dom';



const DrugInputs = () => {

const navigate = useNavigate();

 const [values, setValues] = useState({
   drugname: "",
   drugcategory: "",
   quantity: "",
   dateadded: "",
   expirydate: ""
 })

 const handleSubmit = (event) => {
    event.preventDefault();
    axios.post("http://localhost:8081/druginput", {values}).then(res => {
      console.log(res);
      navigate("/")

    }).catch(err => console.log(err));
 }

  return (
    <div>
        <section className="vh-100 gradient-custom">
    <div className="container py-5 h-100 ">
      <div className="d-flex justify-content-center align-items-center h-100 pt-5">

          <div className="card" style={{borderRadius: '1rem', background: 'white'}}>

            <div className="card-body p-5 text-center">

                <form onSubmit={handleSubmit}>
                <div> Stocks Inputs</div>
                <Box component="form" sx={{'& > :not(style)': { m: 1, width: '25ch' },}}noValidate autoComplete="off">
                <TextField id="outlined-secondary" label="Drug Name" name='drugname' variant="outlined" color="secondary" onChange={event => setValues(event.target.value)}/>
                </Box>

                 <Box component="form" sx={{'& > :not(style)': { m: 1, width: '25ch' },}}noValidate autoComplete="off" >
                 <TextField id="outlined-basic" label="Drug Catergory" name='drugcategory' variant="outlined" color="secondary" onChange={event => setValues(event.target.value)}/>
                 </Box> 

                 <Box component="form" sx={{'& > :not(style)': { m: 1, width: '25ch' },}}noValidate autoComplete="off" >
                 <TextField id="outlined-basic" label="Quantity" variant="outlined" name='quantity' color="secondary" onChange={event => setValues(event.target.value)}/>
                 </Box>


{/*                  
                 <input type='date' name='dateadded' onChange={event => setValues(event.target.value)}></input>
                 <br></br>

                 <input type='date' name='expirydate' onChange={event => setValues(event.target.value)}></input> */}


                 <div className="container-login100-form-btn m-t-17 pt-3">
                 <button type="submit" className="btn btn-success">
                  Submit
                  </button>
                </div>


                </form>


          </div>
        </div>
      </div>
    </div>
  </section>
  </div>

  )
}

export default DrugInputs
