import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import {differenceInDays ,differenceInBusinessDays, differenceInMonths, differenceInCalendarMonths} from 'date-fns'
import { Button } from '@mui/material'

const Showdrugs = () => {


const [drugs, setValues] = useState([""]);
useEffect(() => {
    axios.get("http://localhost:8081/showdrugs").then(res => setValues(res.data)).catch(err => console.log(err))

}, [])

useEffect(()=> {
  Postit();
}, [])

const Postit = async () => {
  await axios.post("http://localhost:8081/showdrugs", filtered).then(res => {
  console.log(res);
}).catch(err => console.log( "Error is here"+err));
}



const filtered = drugs.filter(obj => {

    let myexpiry = obj.ExpiryDate;
    const truncatedDate = new Date(myexpiry).toLocaleDateString('en-CA', {year: 'numeric', month: '2-digit', day: '2-digit'})

   const startDate = new Date();

   const endDate = new Date(truncatedDate);
   console.log(differenceInCalendarMonths(endDate,startDate) + "Month to expire")
   return (differenceInCalendarMonths(endDate,startDate) == 1 || differenceInCalendarMonths(endDate,startDate) <=3)


  });


  return (
    <React.Fragment>

<div className='Show-Drugs'>
    <h2>AVAILABLE DRUGS</h2>     

 <div className='h-100 d-flex align-items-center justify-content-center pt-2'>



 <table className="table table-dark">
  <thead>
    <tr>
      <th scope="col"></th>
      <th scope="col">Drug name</th>
      <th scope="col">Drug Category</th>
      <th scope="col">Quantity</th>
      <th scope="col">Date added</th>
      <th scope="col">Expiry Date</th>
      <th scope='col'></th>

    </tr>
  </thead>
  <tbody>
    {
    drugs.map((data, i) => (

        <tr key={i}>
        <td></td>    
        <td>{data.DrugName} </td>
        <td> {data.DrugCategory}</td>
        <td> {data.Quantity}</td>
        <td> {data.DateAdded}</td>
        <td> {data.ExpiryDate}</td>
        <td> 
            <button className='btn btn-primary'>Update</button>
            <button className='btn btn-danger ms-2'>Delete</button>
        </td>

        </tr>
    ))
    }


  </tbody>
</table>

 </div>
<div className='d-flex align-items-center justify-content-center'>
<Link to="/druginput" className='btn btn-success' style={{width:"130px", }}> Add</Link>
{/* <Button to="/druginput" className='btn btn-success' style={{width:"130px", }} onLoad={Postit()}> Add</Button> */}
</div>
</div> 
    </React.Fragment>
  )
}
export default Showdrugs;
